  <script src="https://code.jquery.com/jquery-3.7.1.min.js" integrity="sha256-/JqT3SQfawRcv/BIHPThkBvs0OEvtFFmqPF/lYI/Cxo=" crossorigin="anonymous"></script>
  <script>
  $(".form-control").on("change", function() {
  var select=$(this).val();
  console.log(select);

  var obj={"apikey": "4f1d83ff-c3cb-4683-8104-938388e47030","id": select};
  var json_f=JSON.stringify(obj);
  console.log(json_f);
$.ajax({
  url: "https://api.cricapi.com/v1/match_info?apikey=4f1d83ff-c3cb-4683-8104-938388e47030&id="+select,
  method: "POST",
  data: json_f,
  success: function(data){
    console.log(data);
    if(data.data.matchStarted==true){var ms="Yes"}else{var ms="No"}
    if(data.data.matchEnded==true){var me="Yes"}else{var me="No"}
    $("#name").html("");
    $("#name").append("<th>Name</th><td>"+data.data.name+"</td>");

    $("#date").html("");
    $("#date").append("<th>Date</th><td>"+data.data.date+"</td>");

    $("#scorest1").html("");
    $("#scorest1").append("<th>"+ data.data.score[0].inning +"</th><td>Runs:"+data.data.score[0].r+"  Overs:"+data.data.score[0].o+"</td>");

    $("#scorest2").html("");
    $("#scorest2").append("<th>"+ data.data.score[1].inning +"</th><td>Runs:"+data.data.score[1].r+"  Overs:"+data.data.score[1].o+"</td>");

    $("#match_started").html("");
    $("#match_started").append("<th>Match Started</th><td>"+ms+"</td>");

    $("#match_ended").html("");
    $("#match_ended").append("<th>Match Ended</th><td>"+me+"</td>");

    $("#match_type").html("");
    $("#match_type").append("<th>Match type</th><td>"+data.data.matchType+"</td>");

    $("#match_winner").html("");
    $("#match_winner").append("<th>Match winner</th><td>"+data.data.matchWinner+"</td>");

    $("#match_winner").html("");
    $("#match_winner").append("<th>Match winner</th><td>"+data.data.matchWinner+"</td>");

    $("#status").html("");
    $("#status").append("<th>Match Status</th><td>"+data.data.status+"</td>");


    $("#toss_choice").html("");
    $("#toss_choice").append("<th>Toss Choice</th><td>"+data.data.tossChoice+"</td>");

    $("#toss_winner").html("");
    $("#toss_winner").append("<th>Toss Winner</th><td>"+data.data.tossWinner+"</td>");
  }
});
});
  </script>
